import React, {useEffect, useState} from 'react';
import TextareaAutosize from 'react-textarea-autosize';
import {CheckIcon, PencilIcon, TrashIcon, XMarkIcon} from "@heroicons/react/24/outline";
import style from './partner.module.scss'
import classNames from "classnames";
const PartnerComp = ({data}) => {
    const [dataPart, setData]=useState([])
    const [hidden, setState] = useState(true)
    const [namePart, setName]=useState("")
    const [descPart, setDesc]=useState("")
    const [readOnly, setRead]=useState(true)
    const classes = classNames({
        hidden:!hidden,
        [style.aprove]:hidden,
    })
    const classesAprove = classNames({
        hidden:hidden,
        [style.aprove]:!hidden,
    })
    useEffect(() => {
        setData(data)
        setName(data[0])
        setDesc(data[1])
    }, []);
    function hideBtn() {
        if (readOnly){
            setRead(false)
        }
        setState(state=>!state)
    }
    function unHideBtn() {
        if (readOnly){
            setRead(true)
        }
        setState(state=>!state)
    }
    return (
        <div className="w-[960px] p-5 rounded-[15px]  bg-white justify-center items-center gap-[50px] inline-flex">
            <img className="w-[156px] h-[97px] p-2.5 rounded-[20px]" src={"http://127.0.0.1:8000/img/"+dataPart[2]}/>
            <div className="grow shrink basis-0 px-2.5 flex-col justify-start items-start gap-2.5 inline-flex">
                <div className="self-stretch justify-start items-center gap-6 inline-flex">
                    <input type="text" className="grow shrink basis-0 text-black text-[28px] font-semibold font-['Sora'] leading-[42px] outline-0" onChange={event=>setName(event.target.value)} readOnly={readOnly} value={namePart}/>
                </div>
                <div
                    className="self-stretch h-fit px-5 pt-2.5 pb-5 bg-white rounded-[20px] border border-stone-900 flex-col justify-start items-start gap-2.5 flex overflow-auto">
                    <div className="self-stretch text-black text-base font-normal font-['Sora'] leading-normal">Описание
                    </div>
                    <TextareaAutosize className="self-stretch text-zinc-800 h-fit text-xl font-normal font-['Sora'] leading-[21px] outline-0 resize-none overflow-hidden" readOnly={readOnly}  value={descPart}/>
                </div>
                <div>
                    <div className={classes}>
                        <button onClick={hideBtn}>
                            <PencilIcon className="size-5"/>
                        </button>
                        <button>
                            <TrashIcon className="size-5 text-red-700"/>
                        </button>
                    </div>
                    <div className={classesAprove}>
                        <button >
                            <CheckIcon className="size-5 text-green-600"/>
                        </button>
                        <button onClick={unHideBtn}>
                            <XMarkIcon className="size-5 text-red-700"/>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PartnerComp;
