import React, {useEffect, useState} from 'react';
import {Routes, Route} from "react-router-dom";
import {ROUTING, ROUTINGADMIN} from "./contsRoute";
import Container from "../componets/layout/container/container";
import Main from "../pages/main";
import "../style.css"
import ContainerAdmin from "../componetsAdmin/layout/container/containerAdmin";
import axios from "axios";
const AppRoute = () => {
    const [authAdmin,setAuthAdmin]=useState(false)
    const [auth,setAuth]=useState(false)
    useEffect(() => {
        axios.get('http://localhost:8000/protected-route/super',{
            headers: {
                'accept': 'application/json'
            },
            withCredentials: true
        }).then((res)=>setAuthAdmin(true)
        ).catch((exp)=>setAuthAdmin(false))
        axios.get('http://localhost:8000/protected-route', {
            headers: {
                'accept': 'application/json'
            },
            withCredentials: true
        }).then((res) => setAuth(true)
        ).catch((exp) => setAuth(false))
    }, []);
    return (
        <Routes>
            <Route element={<Container/>}>
                {ROUTING.map(link => (
                    !auth && link.path==="/lk"?
                    <Route key={link.path}
                           path={link.path}
                           element={link.component}
                    />:
                               <Route key={link.path}
                              path={link.path}
                              element={link.component}/>
                ))}
                <Route path="*" element={
                    <Main/>
                }/>
            </Route>
            {authAdmin?
            <Route element={<ContainerAdmin/>}>
                {ROUTINGADMIN.map(link=>(
                    <Route key={link.path}
                    path={link.path}
                    element={link.component}
                    />
                ))}
            </Route>:null}
        </Routes>
    );
};

export default AppRoute;
