    //                             }
    //                         }
    //                         pc.addEventListener('icegatheringstatechange', checkState);
    //                     }
    //                 });
    //             }).then(function() {
    //                 var offer = pc.localDescription;
    //                 console.log(offer)
    //                 axios.post('http://localhost:8000/rtc/offer_cv', {
    //                         sdp: offer.sdp,
    //                         type: offer.type,
    //                     },{
    //                         headers: {
    //                             'Content-Type': 'application/json'
    //                         }}
    //                 ).then(console.log('ыфв'))
    //             }).then(function(response) {
    //                 return response.json();
    //             }).then(function(answer) {
    //                 return pc.setRemoteDescription(answer);
    //             }).catch(function(e) {
    //                 alert(e);
    //             });
    //         }).catch((err) => {
    //             setError(err.name);
    //         })
    //
    // }
    // const stopStream = () => {
    //     if (streamRef.current) {
    //         streamRef.current.getTracks().forEach((track) => track.stop());
    //     }
    // };

    // useEffect(() => {
    //     setError(null);
    //     stopStream();
    //     if (isEnabled) startStream();
    // }, [isEnabled, facing]);

// peer connection
    var pc = null;

// data channel
    var dc = null, dcInterval = null;

    function createPeerConnection() {
        var config = {
            sdpSemantics: 'unified-plan'
        };

        if (document.getElementById('use-stun').checked) {
            config.iceServers = [{urls: ['stun:stun.l.google.com:19302']}];
        }

        pc = new RTCPeerConnection(config);

        // connect audio / video
        pc.addEventListener('track', function(evt) {
            document.getElementById('video').srcObject = evt.streams[0];
        });

        return pc;
    }

    function negotiate() {
        return pc.createOffer().then(function(offer) {
            return pc.setLocalDescription(offer);
        }).then(function() {
            // wait for ICE gathering to complete
            return new Promise(function(resolve) {
                if (pc.iceGatheringState === 'complete') {
                    resolve();
                } else {
                    function checkState() {
                        if (pc.iceGatheringState === 'complete') {
                            pc.removeEventListener('icegatheringstatechange', checkState);
                            resolve();
                        }
                    }
                    pc.addEventListener('icegatheringstatechange', checkState);
                }
            });
        }).then(function() {
            var offer = pc.localDescription;
            document.getElementById('offer-sdp').textContent = offer.sdp;
            return axios.post('http://127.0.0.1:8000/rtc/offer_cv', {
                    sdp: offer.sdp,
                    type: offer.type,
                    video_transform: document.getElementById('video-transform').value
                },{
                headers: {
                    'Content-Type': 'application/json'
                },
            });
        }).then(function(response) {
            return response.data;
        }).then(function(answer) {
            document.getElementById('answer-sdp').textContent = answer.sdp;
            return pc.setRemoteDescription(answer);
        }).catch(function(e) {
            alert(e);
        });
    }

    function start() {
        document.getElementById('start').style.display = 'none';

        pc = createPeerConnection();

        var constraints = {
            audio: document.getElementById('use-audio').checked,
            video: true
        };
        navigator.mediaDevices.getUserMedia(constraints).then(function(stream) {
            stream.getTracks().forEach(function (track) {
                pc.addTrack(track, stream);
            });
            return negotiate();
        })
    }

    function stop() {
        document.getElementById('stop').style.display = 'none';
        document.getElementById('start').style.display = 'inline-block';
        // close data channel
        if (dc) {
            dc.close();
        }

        // close transceivers
        if (pc.getTransceivers) {
            pc.getTransceivers().forEach(function(transceiver) {
                if (transceiver.stop) {
                    transceiver.stop();
                }
            });
        }

        // close local audio / video
        pc.getSenders().forEach(function(sender) {
            sender.track.stop();
        });

        // close peer connection
        setTimeout(function() {
            pc.close();
        }, 500);
    }

    return (
        <div className="mt-[15px] ml-[15px]">
            {/*<Webcam className="rounded-[15px]" videoConstraints={()=>{facingMode: { exact: "environment" }}></Webcam>*/}
            {/*<video id="video"*/}
