    //     if (streamRef.current) {
    //         streamRef.current.getTracks().forEach((track) => track.stop());
    //     }
    // };

    // useEffect(() => {
    //     setError(null);
    //     stopStream();
    //     if (isEnabled) startStream();
    // }, [isEnabled, facing]);
    var dataChannelLog = document.getElementById('data-channel'),
        iceConnectionLog = document.getElementById('ice-connection-state'),
        iceGatheringLog = document.getElementById('ice-gathering-state'),
        signalingLog = document.getElementById('signaling-state');

// peer connection
    var pc = null;

// data channel
    var dc = null, dcInterval = null;

    function createPeerConnection() {
        var config = {
            sdpSemantics: 'unified-plan'
        };

        if (document.getElementById('use-stun').checked) {
            config.iceServers = [{urls: ['stun:stun.l.google.com:19302']}];
        }

        pc = new RTCPeerConnection(config);

        // connect audio / video
        pc.addEventListener('track', function(evt) {
            if (evt.track.kind == 'video')
                document.getElementById('video').srcObject = evt.streams[0];
            else
                document.getElementById('audio').srcObject = evt.streams[0];
        });

        return pc;
    }

    function negotiate() {
        return pc.createOffer().then(function(offer) {
            return pc.setLocalDescription(offer);
        }).then(function() {
            // wait for ICE gathering to complete
            return new Promise(function(resolve) {
                if (pc.iceGatheringState === 'complete') {
                    resolve();
                } else {
                    function checkState() {
                        if (pc.iceGatheringState === 'complete') {
                            pc.removeEventListener('icegatheringstatechange', checkState);
                            resolve();
                        }
                    }
                    pc.addEventListener('icegatheringstatechange', checkState);
                }
            });
        }).then(function() {
            var offer = pc.localDescription;
            var codec;

            codec = document.getElementById('audio-codec').value;
            if (codec !== 'default') {
                offer.sdp = sdpFilterCodec('audio', codec, offer.sdp);
            }

            codec = document.getElementById('video-codec').value;
            if (codec !== 'default') {
                offer.sdp = sdpFilterCodec('video', codec, offer.sdp);
            }

            document.getElementById('offer-sdp').textContent = offer.sdp;
            return axios.post('http://127.0.0.1:8000/rtc/offer_cv', {
                    sdp: offer.sdp,
                    type: offer.type,
                    video_transform: document.getElementById('video-transform').value
                },{
                headers: {
                    'Content-Type': 'application/json'
                },
            });
        }).then(function(response) {
            return response.data;
        }).then(function(answer) {
            document.getElementById('answer-sdp').textContent = answer.sdp;
            return pc.setRemoteDescription(answer);
        }).catch(function(e) {
            alert(e);
        });
    }

    function start() {
        document.getElementById('start').style.display = 'none';

        pc = createPeerConnection();

        var time_start = null;

        function current_stamp() {
            if (time_start === null) {
                time_start = new Date().getTime();
                return 0;
            } else {
                return new Date().getTime() - time_start;
            }
        }


        var constraints = {
            audio: document.getElementById('use-audio').checked,
            video: true
        };
        navigator.mediaDevices.getUserMedia(constraints).then(function(stream) {
            stream.getTracks().forEach(function (track) {
                pc.addTrack(track, stream);
            });
            return negotiate();
        })
    }

    function stop() {
        document.getElementById('stop').style.display = 'none';
        document.getElementById('start').style.display = 'inline-block';
        // close data channel
        if (dc) {
            dc.close();
        }

        // close transceivers
        if (pc.getTransceivers) {
            pc.getTransceivers().forEach(function(transceiver) {
                if (transceiver.stop) {
                    transceiver.stop();
                }
            });
        }

        // close local audio / video
        pc.getSenders().forEach(function(sender) {
            sender.track.stop();
        });

        // close peer connection
        setTimeout(function() {
            pc.close();
        }, 500);
    }

    function sdpFilterCodec(kind, codec, realSdp) {
        var allowed = []
        var rtxRegex = new RegExp('a=fmtp:(\\d+) apt=(\\d+)\r$');
        var codecRegex = new RegExp('a=rtpmap:([0-9]+) ' + escapeRegExp(codec))
        var videoRegex = new RegExp('(m=' + kind + ' .*?)( ([0-9]+))*\\s*$')

        var lines = realSdp.split('\n');

        var isKind = false;
        for (var i = 0; i < lines.length; i++) {
            if (lines[i].startsWith('m=' + kind + ' ')) {
                isKind = true;
            } else if (lines[i].startsWith('m=')) {
                isKind = false;
            }

            if (isKind) {
                var match = lines[i].match(codecRegex);
                if (match) {
                    allowed.push(parseInt(match[1]));
                }

                match = lines[i].match(rtxRegex);
                if (match && allowed.includes(parseInt(match[2]))) {
                    allowed.push(parseInt(match[1]));
                }
            }
        }

        var skipRegex = 'a=(fmtp|rtcp-fb|rtpmap):([0-9]+)';
        var sdp = '';

        isKind = false;
        for (var i = 0; i < lines.length; i++) {
            if (lines[i].startsWith('m=' + kind + ' ')) {
                isKind = true;
            } else if (lines[i].startsWith('m=')) {
                isKind = false;
            }

            if (isKind) {
                var skipMatch = lines[i].match(skipRegex);
                if (skipMatch && !allowed.includes(parseInt(skipMatch[2]))) {
                    continue;
                } else if (lines[i].match(videoRegex)) {
                    sdp += lines[i].replace(videoRegex, '$1 ' + allowed.join(' ')) + '\n';
                } else {
                    sdp += lines[i] + '\n';
                }
            } else {
                sdp += lines[i] + '\n';
            }
        }

        return sdp;
    }

    function escapeRegExp(string) {
        return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); // $& means the whole matched string
    }
    return (
        <div className="mt-[15px] ml-[15px]">
            {/*<Webcam className="rounded-[15px]" videoConstraints={()=>{facingMode: { exact: "environment" }}></Webcam>*/}
            {/*<video id="video"*/}
            {/*       className={facing === "user" ? "mirror" : ""}*/}
            {/*       playsInline*/}
            {/*       muted*/}
            {/*       autoPlay*/}
            {/*       ref={videoRef}*/}
            {/*></video>*/}
            {/*{error && <div className="error">{error}</div>}*/}
            {/*{isEnabled && <h1>{facing === "user" ? "Front Cam" : "Back Cam"}</h1>}*/}
            {/*<div className="controls">*/}
            {/*    <button onClick={() => setEnabled(!isEnabled)}>*/}
            {/*        {isEnabled ? "Off" : "ON"}*/}
            {/*    </button>*/}
            {/*    <button*/}
            {/*        onClick={() => setFacing(facing === "user" ? "environment" : "user")}*/}
            {/*    >*/}
            {/*        Перевернуть*/}
            {/*    </button>*/}
            {/*</div>*/}
            <h2>Options</h2>
            <div className="option">
                <input id="use-datachannel" checked="checked" type="checkbox"/>
                <label htmlFor="use-datachannel">Use datachannel</label>
                <select id="datachannel-parameters">
                    <option value='{"ordered": true}'>Ordered, reliable</option>
                    <option value='{"ordered": false, "maxRetransmits": 0}'>Unordered, no retransmissions</option>
                    <option value='{"ordered": false, "maxPacketLifetime": 500}'>Unordered, 500ms lifetime</option>
                </select>
            </div>
            <div className="option">
                <input id="use-audio" checked="checked" type="checkbox"/>
                <label htmlFor="use-audio">Use audio</label>
                <select id="audio-codec">
                    <option value="default" selected>Default codecs</option>
                    <option value="opus/48000/2">Opus</option>
                    <option value="PCMU/8000">PCMU</option>
                    <option value="PCMA/8000">PCMA</option>
                </select>
            </div>
            <div className="option">
                <input id="use-video" type="checkbox"/>
                <label htmlFor="use-video">Use video</label>
                <select id="video-resolution">
                    <option value="" selected>Default resolution</option>
                    <option value="320x240">320x240</option>
                    <option value="640x480">640x480</option>
                    <option value="960x540">960x540</option>
                    <option value="1280x720">1280x720</option>
                </select>
                <select id="video-transform">
                    <option value="none" selected>No transform</option>
                    <option value="edges">Edge detection</option>
                    <option value="cartoon">Cartoon effect</option>
                    <option value="rotate">Rotate</option>
                    <option value="cv">CV</option>
                </select>
                <select id="video-codec">
                    <option value="default" selected>Default codecs</option>
                    <option value="VP8/90000">VP8</option>
                    <option value="H264/90000">H264</option>
                </select>
            </div>
            <div className="option">
                <input id="use-stun" type="checkbox"/>
                <label htmlFor="use-stun">Use STUN server</label>
            </div>

            <button id="start" onClick={start}>Start</button>
            <button id="stop" >Stop</button>

            <h2>State</h2>
            <p>
                ICE gathering state: <span id="ice-gathering-state"></span>
            </p>
            <p>
                ICE connection state: <span id="ice-connection-state"></span>
            </p>
            <p>
                Signaling state: <span id="signaling-state"></span>
            </p>

            <div id="media" >
                <h2>Media</h2>

                <audio id="audio" autoPlay="true"></audio>
                <video id="video" autoPlay="true" playsInline="true"></video>
            </div>

            <h2>Data channel</h2>
            <pre id="data-channel" ></pre>

            <h2>SDP</h2>

            <h3>Offer</h3>
            <pre id="offer-sdp"></pre>

            <h3>Answer</h3>
            <pre id="answer-sdp"></pre>
        </div>
    );
}

