import React, {useEffect, useRef, useState} from 'react';
import Webcam from "react-webcam";
import axios from "axios";

const MainRtc = () => {
    const [error, setError] = useState();
    const [isEnabled, setEnabled] = useState(false);
    const [facing, setFacing] = useState("user");
    const videoRef = useRef(null);
    const streamRef = useRef(null);

    // const startStream = () => {
    //     navigator.mediaDevices
    //         .getUserMedia({
    //             audio: false,
    //             video: {
    //                 facingMode: { exact: facing },
    //             },
    //         })
    //         .then((stream) => {
    //             streamRef.current = stream;
    //             videoRef.current.srcObject = streamRef.current;
    //             videoRef.current.onloadedmetadata = () => videoRef.current.play();
    //             var config = {
    //                 sdpSemantics: 'unified-plan'
    //             };
    //
    //
    //             const pc = connect()
    //
    //             pc.createOffer().
    //             then(function(offer) {
    //                  pc.setLocalDescription(offer);
    //             }).then(function() {
    //                 // wait for ICE gathering to complete
    //                 new Promise(function(resolve) {
    //                     if (pc.iceGatheringState === 'complete') {
    //                         resolve();
    //
    //                     } else {
    //                         function checkState() {
    //                             if (pc.iceGatheringState === 'complete') {
    //                                 pc.removeEventListener('icegatheringstatechange', checkState);
    //                                 resolve();
    //                             }
    //                         }
    //                         pc.addEventListener('icegatheringstatechange', checkState);
    //                     }
    //                 });
    //             }).then(function() {
    //                 var offer = pc.localDescription;
    //                 console.log(offer)
    //                 axios.post('http://localhost:8000/rtc/offer_cv', {
    //                         sdp: offer.sdp,
    //                         type: offer.type,
    //                     },{
    //                         headers: {
    //                             'Content-Type': 'application/json'
    //                         }}
    //                 ).then(console.log('ыфв'))
    //             }).then(function(response) {
    //                 return response.json();
    //             }).then(function(answer) {
    //                 return pc.setRemoteDescription(answer);
    //             }).catch(function(e) {
    //                 alert(e);
    //             });
    //         }).catch((err) => {
    //             setError(err.name);
    //         })
    //
    // }
    // const stopStream = () => {
    //     if (streamRef.current) {
    //         streamRef.current.getTracks().forEach((track) => track.stop());
    //     }
    // };

    // useEffect(() => {
    //     setError(null);
    //     stopStream();
    //     if (isEnabled) startStream();
    // }, [isEnabled, facing]);

    return (
        <div className="mt-[15px] ml-[15px]">
            {/*<Webcam className="rounded-[15px]" videoConstraints={()=>{facingMode: { exact: "environment" }}></Webcam>*/}
            {/*<video id="video"*/}
            {/*       className={facing === "user" ? "mirror" : ""}*/}
            {/*       playsInline*/}
            {/*       muted*/}
            {/*       autoPlay*/}
            {/*       ref={videoRef}*/}
            {/*></video>*/}
            {/*{error && <div className="error">{error}</div>}*/}
            {/*{isEnabled && <h1>{facing === "user" ? "Front Cam" : "Back Cam"}</h1>}*/}
            {/*<div className="controls">*/}
            {/*    <button onClick={() => setEnabled(!isEnabled)}>*/}
            {/*        {isEnabled ? "Off" : "ON"}*/}
            {/*    </button>*/}
            {/*    <button*/}
            {/*        onClick={() => setFacing(facing === "user" ? "environment" : "user")}*/}
            {/*    >*/}
            {/*        Перевернуть*/}
            {/*    </button>*/}
            {/*</div>*/}
            <h2>Options</h2>
            <div className="option">
                <input id="use-datachannel" checked="checked" type="checkbox"/>
                <label htmlFor="use-datachannel">Use datachannel</label>
                <select id="datachannel-parameters">
                    <option value='{"ordered": true}'>Ordered, reliable</option>
                    <option value='{"ordered": false, "maxRetransmits": 0}'>Unordered, no retransmissions</option>
                    <option value='{"ordered": false, "maxPacketLifetime": 500}'>Unordered, 500ms lifetime</option>
                </select>
            </div>
            <div className="option">
                <input id="use-audio" checked="checked" type="checkbox"/>
                <label htmlFor="use-audio">Use audio</label>
                <select id="audio-codec">
                    <option value="default" selected>Default codecs</option>
                    <option value="opus/48000/2">Opus</option>
                    <option value="PCMU/8000">PCMU</option>
                    <option value="PCMA/8000">PCMA</option>
                </select>
            </div>
            <div className="option">
                <input id="use-video" type="checkbox"/>
                <label htmlFor="use-video">Use video</label>
                <select id="video-resolution">
                    <option value="" selected>Default resolution</option>
                    <option value="320x240">320x240</option>
                    <option value="640x480">640x480</option>
                    <option value="960x540">960x540</option>
                    <option value="1280x720">1280x720</option>
                </select>
                <select id="video-transform">
                    <option value="none" selected>No transform</option>
                    <option value="edges">Edge detection</option>
                    <option value="cartoon">Cartoon effect</option>
                    <option value="rotate">Rotate</option>
                    <option value="cv">CV</option>
                </select>
                <select id="video-codec">
                    <option value="default" selected>Default codecs</option>
                    <option value="VP8/90000">VP8</option>
                    <option value="H264/90000">H264</option>
                </select>
            </div>
            <div className="option">
                <input id="use-stun" type="checkbox"/>
                <label htmlFor="use-stun">Use STUN server</label>
            </div>

            <button id="start">Start</button>
            <button id="stop" >Stop</button>

            <h2>State</h2>
            <p>
                ICE gathering state: <span id="ice-gathering-state"></span>
            </p>
            <p>
                ICE connection state: <span id="ice-connection-state"></span>
            </p>
            <p>
                Signaling state: <span id="signaling-state"></span>
            </p>

            <div id="media" >
                <h2>Media</h2>

                <audio id="audio" autoPlay="true"></audio>
                <video id="video" autoPlay="true" playsInline="true"></video>
            </div>

            <h2>Data channel</h2>
            <pre id="data-channel" ></pre>

            <h2>SDP</h2>

            <h3>Offer</h3>
            <pre id="offer-sdp"></pre>

            <h3>Answer</h3>
            <pre id="answer-sdp"></pre>
        </div>
    );
}

export default MainRtc;
